import React from 'react';
import { View, Text, Button, StyleSheet, FlatList, SafeAreaView, TouchableOpacity, Modal, Alert } from 'react-native';

import CreateNotePage from './CreateNotePage';
import { Card } from 'react-native-paper';

const Item = ({ item, onPress, style }) => (
  <TouchableOpacity onPress={onPress} style={styles.item}>
    <Card style={styles.cardStyle}>
      <View style={styles.cardText}>
        <Text style={styles.textId}>{item.id}</Text>
        <Text style={styles.title}>{item.title}</Text>
      </View>
    </Card>
  </TouchableOpacity>
);

export default function NoteList({navigation}){
  const [modalVisible, setModalVisible] = React.useState(false);
  const [noteList, setNoteList] = React.useState([{title: 'hello', id: 0, text: 'heyo'}]);

  /*const appendNote = (note) => {
    setNoteList(()=>{
      let temp = noteList;
      temp.push(note);
      return temp;
    })
  }*/
  const renderItem = ({ item }) => {

    return (
      <Item
        item={item}
        onPress={() => {console.log('hi')}}
      />
    );
  };

  return(
    <View style={styles.container}>
      {
        modalVisible ? 
        <CreateNotePage 
          setNoteList={setNoteList}
          setModalVisible={setModalVisible}
          noteList={noteList}
        /> :
        <View style={{flex: 1}}>
        <SafeAreaView style={styles.flatList}>
          {
            (noteList.length>0)? 
            <FlatList
              data={noteList}
              renderItem={renderItem}
              keyExtractor={(item) => item.id}
            />
            :<Text>No Notes Saved!</Text>
          }
        </SafeAreaView>
        <Button style={styles.button} title='Add Note' onPress={()=>setModalVisible(true)} />
        </View>
      }
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  item: {
    flex: 1
  },
  flatList: {
    flex: 5
  },
  button: {
    flex: 1
  },
  title: {
    flex: 1,
    fontWeight: '600',
    fontSize: 36
  },
  textId: {
    flex: 1,
    fontWeight: '400',
    fontSize: 24,
    textAlignVertical: 'center',
    paddingRight: 20
  },
  cardStyle: {
    padding: 15,
    margin: 10,
    backgroundColor: '#55e02b'
  },
  cardText: {
    flexDirection: 'row',
    justifyContent: 'center'
  }
})
